import React, { useState, useEffect } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameDay, addMonths, subMonths, isToday } from 'date-fns';
import { CalendarDays, ChevronLeft, ChevronRight, CreditCard, Users, Mail, Loader2, Plus, Search, Settings, LogOut, Truck, Wallet, Calendar as CalendarIcon } from 'lucide-react';

// Mock data - replace with API calls later
const MOCK_BOOKINGS = [
  {
    id: "KR-1027",
    customer: { name: "Sarah Thompson", email: "sarah.t@example.com", phone: "+44 7700 900123", address: "27 Beech Close, EN10" },
    pod: "16ft Pod",
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 22),
    endDate: new Date(new Date().getFullYear(), new Date().getMonth(), 29),
    status: "Confirmed",
    payment: { status: "Deposit paid", amount: 300, currency: "GBP", method: "Card" },
    delivery: { date: new Date(new Date().getFullYear(), new Date().getMonth(), 22, 9, 30), driver: "Self", notes: "Narrow driveway. Use motor mover." },
    notes: "Customer requested kettle/toaster/microwave pack.",
  },
  {
    id: "KR-1028",
    customer: { name: "James Patel", email: "james.p@example.com", phone: "+44 7700 900456", address: "41 Oak Road, SG12" },
    pod: "10ft Pod",
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 31),
    endDate: addMonths(new Date(new Date().getFullYear(), new Date().getMonth(), 7), 1),
    status: "Awaiting deposit",
    payment: { status: "Pending", amount: 300, currency: "GBP", method: "Link" },
    delivery: { date: new Date(new Date().getFullYear(), new Date().getMonth(), 31, 13, 0), driver: "Contractor", notes: "Steep kerb. Bring ramps." },
    notes: "Send insurance docs once paid.",
  },
];

const STATUS_MAP: Record<string, { color: string }> = {
  Confirmed: { color: "bg-emerald-100 text-emerald-700" },
  "Awaiting deposit": { color: "bg-amber-100 text-amber-700" },
  Cancelled: { color: "bg-rose-100 text-rose-700" },
};

interface Booking {
  id: string;
  customer: {
    name: string;
    email: string;
    phone: string;
    address: string;
  };
  pod: string;
  startDate: Date;
  endDate: Date;
  status: string;
  payment: {
    status: string;
    amount: number;
    currency: string;
    method: string;
  };
  delivery: {
    date: Date;
    driver: string;
    notes: string;
  };
  notes: string;
}

function MonthCalendar({
  month,
  bookings,
  onSelectBooking,
}: {
  month: Date;
  bookings: Booking[];
  onSelectBooking: (bId: string) => void;
}) {
  const days = eachDayOfInterval({ start: startOfMonth(month), end: endOfMonth(month) });

  return (
    <div className="grid grid-cols-7 gap-2">
      {["Mon","Tue","Wed","Thu","Fri","Sat","Sun"].map((d) => (
        <div key={d} className="text-xs font-medium text-gray-500 text-center">{d}</div>
      ))}
      {days.map((day) => (
        <div key={+day} className={`min-h-[90px] rounded-xl border p-2 relative ${isToday(day) ? "border-blue-500" : "border-gray-200"}`}>
          <div className="text-xs font-semibold">{format(day, "d")}</div>
          <div className="mt-1 space-y-1">
            {bookings
              .filter((b) => day >= startOfMonth(month) && day <= endOfMonth(month) && day >= b.startDate && day <= b.endDate)
              .map((b) => (
                <button
                  key={b.id}
                  onClick={() => onSelectBooking(b.id)}
                  className={`w-full text-left text-[11px] px-2 py-1 rounded-md truncate ${STATUS_MAP[b.status]?.color || "bg-gray-100 text-gray-700"}`}
                  title={`${b.id} • ${b.customer.name}`}
                >
                  {b.pod} — {b.customer.name.split(" ")[0]}
                </button>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
}

function Stat({ icon: Icon, label, value }: { icon: any; label: string; value: string | number }) {
  return (
    <div className="bg-white rounded-lg shadow-sm border p-4">
      <div className="flex items-center justify-between mb-2">
        <h3 className="text-sm font-medium text-gray-500">{label}</h3>
        <Icon className="h-4 w-4 text-gray-400" />
      </div>
      <div className="text-2xl font-bold">{value}</div>
    </div>
  );
}

function Button({ children, className = "", onClick, variant = "default", size = "md", ...props }: any) {
  const baseClasses = "inline-flex items-center justify-center rounded-md font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2";
  const variants: Record<string, string> = {
    default: "bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-500",
    outline: "border border-gray-300 bg-white text-gray-700 hover:bg-gray-50 focus:ring-blue-500",
    ghost: "text-gray-700 hover:bg-gray-100 focus:ring-blue-500",
    destructive: "bg-red-600 text-white hover:bg-red-700 focus:ring-red-500"
  };
  const sizes: Record<string, string> = {
    sm: "h-8 px-3 text-sm",
    md: "h-10 px-4 text-sm",
    lg: "h-11 px-8 text-sm"
  };

  return (
    <button
      className={`${baseClasses} ${variants[variant]} ${sizes[size]} ${className}`}
      onClick={onClick}
      {...props}
    >
      {children}
    </button>
  );
}

function Input({ className = "", ...props }: any) {
  return (
    <input
      className={`flex h-10 w-full rounded-md border border-gray-300 bg-white px-3 py-2 text-sm placeholder:text-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${className}`}
      {...props}
    />
  );
}

function Label({ children, className = "", ...props }: any) {
  return (
    <label className={`text-sm font-medium text-gray-700 ${className}`} {...props}>
      {children}
    </label>
  );
}

function Badge({ children, className = "", ...props }: any) {
  return (
    <span className={`inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-medium ${className}`} {...props}>
      {children}
    </span>
  );
}

function Table({ children, className = "" }: any) {
  return (
    <div className={`overflow-hidden rounded-lg border border-gray-200 ${className}`}>
      <table className="min-w-full divide-y divide-gray-200">
        {children}
      </table>
    </div>
  );
}

function TableHeader({ children }: any) {
  return <thead className="bg-gray-50">{children}</thead>;
}

function TableBody({ children }: any) {
  return <tbody className="bg-white divide-y divide-gray-200">{children}</tbody>;
}

function TableRow({ children, className = "", onClick }: any) {
  return (
    <tr className={`hover:bg-gray-50 ${onClick ? 'cursor-pointer' : ''} ${className}`} onClick={onClick}>
      {children}
    </tr>
  );
}

function TableHead({ children, className = "" }: any) {
  return (
    <th className={`px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider ${className}`}>
      {children}
    </th>
  );
}

function TableCell({ children, className = "" }: any) {
  return (
    <td className={`px-6 py-4 whitespace-nowrap text-sm text-gray-900 ${className}`}>
      {children}
    </td>
  );
}

function Dialog({ children, open, onOpenChange }: any) {
  if (!open) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      <div className="fixed inset-0 bg-black bg-opacity-50" onClick={() => onOpenChange(false)} />
      <div className="relative bg-white rounded-lg shadow-lg max-w-md w-full mx-4">
        {children}
      </div>
    </div>
  );
}

function DialogContent({ children, className = "" }: any) {
  return (
    <div className={`p-6 ${className}`}>
      {children}
    </div>
  );
}

function DialogHeader({ children }: any) {
  return <div className="mb-4">{children}</div>;
}

function DialogTitle({ children, className = "" }: any) {
  return <h2 className={`text-lg font-semibold text-gray-900 ${className}`}>{children}</h2>;
}

function DialogDescription({ children, className = "" }: any) {
  return <p className={`text-sm text-gray-500 ${className}`}>{children}</p>;
}

function DialogFooter({ children, className = "" }: any) {
  return <div className={`flex justify-end space-x-2 mt-6 ${className}`}>{children}</div>;
}

function DialogTrigger({ children, asChild }: any) {
  return children;
}

function Textarea({ className = "", ...props }: any) {
  return (
    <textarea
      className={`flex min-h-[80px] w-full rounded-md border border-gray-300 bg-white px-3 py-2 text-sm placeholder:text-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${className}`}
      {...props}
    />
  );
}

function Sheet({ children, open, onOpenChange }: any) {
  if (!open) return null;

  return (
    <div className="fixed inset-0 z-50 flex">
      <div className="fixed inset-0 bg-black bg-opacity-50" onClick={() => onOpenChange(false)} />
      <div className="relative ml-auto bg-white shadow-lg w-[520px] h-full overflow-y-auto">
        {children}
      </div>
    </div>
  );
}

function SheetContent({ children, side = "right" }: any) {
  return <div className="p-6">{children}</div>;
}

function SheetHeader({ children }: any) {
  return <div className="mb-6">{children}</div>;
}

function SheetTitle({ children, className = "" }: any) {
  return <h2 className={`text-xl font-semibold text-gray-900 ${className}`}>{children}</h2>;
}

function SheetDescription({ children, className = "" }: any) {
  return <p className={`text-sm text-gray-500 ${className}`}>{children}</p>;
}

export default function KitchenRescueAdmin() {
  const [month, setMonth] = useState(new Date());
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState<string | null>(null);
  const [bookings, setBookings] = useState<Booking[]>(MOCK_BOOKINGS);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showNewBookingDialog, setShowNewBookingDialog] = useState(false);

  const filtered = bookings.filter((b) => {
    if (!query?.trim()) return true;
    const q = query.toLowerCase();
    return [b.id, b.customer.name, b.customer.email, b.customer.address, b.pod, b.status]
      .join(" ")
      .toLowerCase()
      .includes(q);
  });

  const selected = bookings.find((b) => b.id === selectedId) || null;

  // Simple login check - replace with proper authentication
  useEffect(() => {
    const token = localStorage.getItem('admin_token');
    setIsLoggedIn(!!token);
  }, []);

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    const username = formData.get('username');
    const password = formData.get('password');

    // Simple hardcoded login - replace with proper authentication
    if (username === 'admin' && password === 'kitchenrescue2024') {
      localStorage.setItem('admin_token', 'dummy_token');
      setIsLoggedIn(true);
    } else {
      alert('Invalid credentials');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('admin_token');
    setIsLoggedIn(false);
  };

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-6">
          <div className="flex items-center gap-2 mb-6">
            <Truck className="h-6 w-6 text-blue-600" />
            <h1 className="text-xl font-semibold">Kitchen Rescue Admin</h1>
          </div>

          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                name="username"
                type="text"
                placeholder="Enter username"
                required
              />
            </div>
            <div>
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                name="password"
                type="password"
                placeholder="Enter password"
                required
              />
            </div>
            <Button type="submit" className="w-full">
              Sign In
            </Button>
          </form>

          <div className="mt-4 text-xs text-gray-500 text-center">
            Demo credentials: admin / kitchenrescue2024
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Top bar */}
      <header className="sticky top-0 z-20 border-b bg-white">
        <div className="mx-auto max-w-7xl px-4 py-3 flex items-center gap-3">
          <div className="flex items-center gap-2">
            <Truck className="h-5 w-5 text-blue-600" />
            <span className="font-semibold">Kitchen Rescue — Admin</span>
          </div>
          <div className="ml-auto flex items-center gap-2">
            <Button variant="ghost" size="sm" title="Settings">
              <Settings className="h-4 w-4"/>
            </Button>
            <Button variant="outline" size="sm" className="gap-2" onClick={handleLogout}>
              <LogOut className="h-4 w-4"/>Log out
            </Button>
          </div>
        </div>
      </header>

      {/* Content */}
      <main className="mx-auto max-w-7xl p-4">
        {/* KPI row */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mb-4">
          <Stat icon={CalendarIcon} label="Pods on hire" value={bookings.length} />
          <Stat icon={CreditCard} label="Deposits pending" value={bookings.filter(b=>b.payment.status!=="Deposit paid").length} />
          <Stat icon={Users} label="Customers" value={new Set(bookings.map(b=>b.customer.email)).size} />
          <Stat icon={Wallet} label="This month revenue" value={`£${bookings.reduce((s,b)=> s + (b.payment.status==="Deposit paid"? b.payment.amount:0),0).toFixed(0)}`} />
        </div>

        {/* Toolbar */}
        <div className="flex flex-col md:flex-row md:items-center gap-2 mb-4">
          <div className="relative w-full md:w-72">
            <Search className="absolute left-2 top-2.5 h-4 w-4 text-gray-400"/>
            <Input 
              value={query} 
              onChange={(e: React.ChangeEvent<HTMLInputElement>)=>setQuery(e.target.value)} 
              placeholder="Search bookings, customers, addresses…" 
              className="pl-8"
            />
          </div>

          <div className="ml-auto flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={()=>setMonth(subMonths(month,1))}>
              <ChevronLeft className="h-4 w-4"/>
            </Button>
            <div className="px-2 text-sm font-medium w-44 text-center">{format(month, "MMMM yyyy")}</div>
            <Button variant="outline" size="sm" onClick={()=>setMonth(addMonths(month,1))}>
              <ChevronRight className="h-4 w-4"/>
            </Button>

            <Button size="sm" className="gap-2" onClick={() => setShowNewBookingDialog(true)}>
              <Plus className="h-4 w-4"/>New booking
            </Button>
          </div>
        </div>

        {/* Two-column layout: calendar + lists */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
          <div className="lg:col-span-2 bg-white rounded-lg shadow-sm border">
            <div className="p-6">
              <h2 className="text-lg font-semibold flex items-center gap-2 mb-2">
                <CalendarDays className="h-5 w-5"/> Calendar
              </h2>
              <p className="text-sm text-gray-500 mb-4">Month view of bookings. Click a booking to view details.</p>
              <MonthCalendar month={month} bookings={filtered} onSelectBooking={setSelectedId} />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm border">
            <div className="p-6">
              <h2 className="text-lg font-semibold flex items-center gap-2 mb-2">
                <CreditCard className="h-5 w-5"/> Payments
              </h2>
              <p className="text-sm text-gray-500 mb-4">Recent deposits & balances</p>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Booking</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead className="text-right">Amount</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {bookings.map((b) => (
                    <TableRow key={b.id} onClick={()=>setSelectedId(b.id)}>
                      <TableCell className="font-medium">{b.id}</TableCell>
                      <TableCell>
                        <Badge className={STATUS_MAP[b.status]?.color}>{b.payment.status}</Badge>
                      </TableCell>
                      <TableCell className="text-right">£{b.payment.amount.toFixed(0)}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          </div>
        </div>

        {/* Customers table */}
        <div className="bg-white rounded-lg shadow-sm border mt-4">
          <div className="p-6">
            <h2 className="text-lg font-semibold flex items-center gap-2 mb-2">
              <Users className="h-5 w-5"/> Customers
            </h2>
            <p className="text-sm text-gray-500 mb-4">Contact details and current hire info</p>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Customer</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Phone</TableHead>
                  <TableHead>Address</TableHead>
                  <TableHead>Booking</TableHead>
                  <TableHead>Status</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filtered.map((b) => (
                  <TableRow key={b.id}>
                    <TableCell className="font-medium">{b.customer.name}</TableCell>
                    <TableCell className="text-xs flex items-center gap-1">
                      <Mail className="h-3 w-3"/>{b.customer.email}
                    </TableCell>
                    <TableCell className="text-xs">{b.customer.phone}</TableCell>
                    <TableCell className="text-xs">{b.customer.address}</TableCell>
                    <TableCell className="text-xs">
                      {b.pod}
                      <div className="text-gray-500">{format(b.startDate, "d MMM")} – {format(b.endDate, "d MMM")}</div>
                    </TableCell>
                    <TableCell>
                      <Badge className={STATUS_MAP[b.status]?.color}>{b.status}</Badge>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </div>
      </main>

      {/* New Booking Dialog */}
      <Dialog open={showNewBookingDialog} onOpenChange={setShowNewBookingDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Create booking</DialogTitle>
            <DialogDescription>Add a new temporary kitchen hire.</DialogDescription>
          </DialogHeader>
          <div className="grid gap-3">
            <div className="grid grid-cols-2 gap-3">
              <div>
                <Label>Customer name</Label>
                <Input placeholder="Full name" />
              </div>
              <div>
                <Label>Email</Label>
                <Input placeholder="email@domain.com" />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-3">
              <div>
                <Label>Phone</Label>
                <Input placeholder="+44" />
              </div>
              <div>
                <Label>Address</Label>
                <Input placeholder="Delivery address" />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-3">
              <div>
                <Label>Pod</Label>
                <select className="w-full h-10 rounded-md border border-gray-300 bg-white px-3 text-sm">
                  <option>16ft Pod</option>
                  <option>10ft Pod</option>
                </select>
              </div>
              <div>
                <Label>Status</Label>
                <select className="w-full h-10 rounded-md border border-gray-300 bg-white px-3 text-sm">
                  <option>Awaiting deposit</option>
                  <option>Confirmed</option>
                  <option>Cancelled</option>
                </select>
              </div>
            </div>
            <div className="grid grid-cols-2 gap-3">
              <div>
                <Label>Start date</Label>
                <Input type="date" />
              </div>
              <div>
                <Label>End date</Label>
                <Input type="date" />
              </div>
            </div>
            <div>
              <Label>Notes</Label>
              <Textarea placeholder="Access notes, packs, other details"/>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowNewBookingDialog(false)}>Cancel</Button>
            <Button>Save booking</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Booking Details Sheet */}
      <Sheet open={!!selected} onOpenChange={(o: boolean)=>!o && setSelectedId(null)}>
        <SheetContent>
          {selected ? (
            <div className="space-y-4">
              <SheetHeader>
                <SheetTitle>{selected.id} • {selected.pod}</SheetTitle>
                <SheetDescription>
                  {format(selected.startDate, "EEE d MMM yyyy")} – {format(selected.endDate, "EEE d MMM yyyy")} ({selected.status})
                </SheetDescription>
              </SheetHeader>

              <div className="grid grid-cols-2 gap-3">
                <div className="bg-gray-50 rounded-lg p-4">
                  <h3 className="text-sm font-semibold mb-2">Customer</h3>
                  <div className="text-sm space-y-1">
                    <div className="font-medium">{selected.customer.name}</div>
                    <div className="flex items-center gap-2 text-gray-500 text-xs">
                      <Mail className="h-3 w-3"/>{selected.customer.email}
                    </div>
                    <div className="text-xs">{selected.customer.phone}</div>
                    <div className="text-xs">{selected.customer.address}</div>
                  </div>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <h3 className="text-sm font-semibold mb-2">Payment</h3>
                  <div className="text-sm space-y-1">
                    <div>Status: <Badge className="align-middle ml-1">{selected.payment.status}</Badge></div>
                    <div>Deposit: £{selected.payment.amount.toFixed(0)} ({selected.payment.method})</div>
                    <div className="text-xs text-gray-500">Balance auto-invoice 7 days before delivery</div>
                    <div className="pt-2 flex gap-2">
                      <Button size="sm" variant="outline">Send payment link</Button>
                      <Button size="sm">Mark as paid</Button>
                    </div>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 rounded-lg p-4">
                <h3 className="text-sm font-semibold mb-2">Delivery</h3>
                <div className="text-sm space-y-1">
                  <div>Date/time: {format(selected.delivery.date, "EEE d MMM, HH:mm")}</div>
                  <div>Driver: {selected.delivery.driver}</div>
                  <div className="text-xs text-gray-500">{selected.delivery.notes}</div>
                  <div className="pt-2 flex gap-2">
                    <Button size="sm" variant="outline">Assign driver</Button>
                    <Button size="sm" variant="outline">Export job sheet</Button>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 rounded-lg p-4">
                <h3 className="text-sm font-semibold mb-2">Internal notes</h3>
                <Textarea defaultValue={selected.notes} className="min-h-[100px]"/>
                <div className="pt-2 text-right">
                  <Button size="sm">Save notes</Button>
                </div>
              </div>

              <div className="flex justify-between">
                <Button variant="destructive" size="sm">Cancel booking</Button>
                <div className="flex gap-2">
                  <Button variant="outline" size="sm">Email customer</Button>
                  <Button size="sm">Edit</Button>
                </div>
              </div>
            </div>
          ) : (
            <div className="flex items-center gap-2 text-gray-500">
              <Loader2 className="h-4 w-4 animate-spin"/> Loading…
            </div>
          )}
        </SheetContent>
      </Sheet>
    </div>
  );
}
