import React, { useState, useEffect } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, addMonths, subMonths, isToday } from 'date-fns';
import { 
  CalendarDays, 
  ChevronLeft, 
  ChevronRight, 
  CreditCard, 
  Users, 
  Mail, 
  Loader2, 
  Plus, 
  Search, 
  Settings, 
  LogOut, 
  Truck, 
  Wallet, 
  Calendar as CalendarIcon,
  MapPin,
  Phone,
  Clock
} from 'lucide-react';

import { Button } from './components/ui/button';
import { Input } from './components/ui/input';
import { Label } from './components/ui/label';
import { Badge } from './components/ui/badge';
import { Textarea } from './components/ui/textarea';
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogFooter, 
  DialogHeader, 
  DialogTitle 
} from './components/ui/dialog';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from './components/ui/table';

// Mock data - replace with API calls later
const MOCK_BOOKINGS = [
  {
    id: "KR-1027",
    customer: { name: "Sarah Thompson", email: "sarah.t@example.com", phone: "+44 7700 900123", address: "27 Beech Close, EN10" },
    pod: "16ft Pod",
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 22),
    endDate: new Date(new Date().getFullYear(), new Date().getMonth(), 29),
    status: "Confirmed",
    payment: { status: "Deposit paid", amount: 300, currency: "GBP", method: "Card" },
    delivery: { date: new Date(new Date().getFullYear(), new Date().getMonth(), 22, 9, 30), driver: "Self", notes: "Narrow driveway. Use motor mover." },
    notes: "Customer requested kettle/toaster/microwave pack.",
  },
  {
    id: "KR-1028",
    customer: { name: "James Patel", email: "james.p@example.com", phone: "+44 7700 900456", address: "41 Oak Road, SG12" },
    pod: "10ft Pod",
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 31),
    endDate: addMonths(new Date(new Date().getFullYear(), new Date().getMonth(), 7), 1),
    status: "Awaiting deposit",
    payment: { status: "Pending", amount: 300, currency: "GBP", method: "Link" },
    delivery: { date: new Date(new Date().getFullYear(), new Date().getMonth(), 31, 13, 0), driver: "Contractor", notes: "Steep kerb. Bring ramps." },
    notes: "Send insurance docs once paid.",
  },
  {
    id: "KR-1029",
    customer: { name: "Emma Wilson", email: "emma.w@example.com", phone: "+44 7700 900789", address: "15 Maple Street, CB1" },
    pod: "16ft Pod",
    startDate: new Date(new Date().getFullYear(), new Date().getMonth() + 1, 5),
    endDate: new Date(new Date().getFullYear(), new Date().getMonth() + 1, 12),
    status: "Confirmed",
    payment: { status: "Deposit paid", amount: 350, currency: "GBP", method: "Card" },
    delivery: { date: new Date(new Date().getFullYear(), new Date().getMonth() + 1, 5, 10, 0), driver: "Contractor", notes: "Flat access. Easy delivery." },
    notes: "Customer needs induction hob pack.",
  },
];

const STATUS_MAP: Record<string, { color: string; variant: "default" | "secondary" | "destructive" | "success" | "warning" | "info" }> = {
  Confirmed: { color: "bg-emerald-50 text-emerald-700 border-emerald-200", variant: "success" },
  "Awaiting deposit": { color: "bg-amber-50 text-amber-700 border-amber-200", variant: "warning" },
  Cancelled: { color: "bg-red-50 text-red-700 border-red-200", variant: "destructive" },
};

interface Booking {
  id: string;
  customer: {
    name: string;
    email: string;
    phone: string;
    address: string;
  };
  pod: string;
  startDate: Date;
  endDate: Date;
  status: string;
  payment: {
    status: string;
    amount: number;
    currency: string;
    method: string;
  };
  delivery: {
    date: Date;
    driver: string;
    notes: string;
  };
  notes: string;
}

function MonthCalendar({
  month,
  bookings,
  onSelectBooking,
}: {
  month: Date;
  bookings: Booking[];
  onSelectBooking: (bId: string) => void;
}) {
  const days = eachDayOfInterval({ start: startOfMonth(month), end: endOfMonth(month) });

  return (
    <div className="grid grid-cols-7 gap-2">
      {["Mon","Tue","Wed","Thu","Fri","Sat","Sun"].map((d) => (
        <div key={d} className="text-xs font-medium text-muted-foreground text-center p-2">
          {d}
        </div>
      ))}
      {days.map((day) => (
        <div 
          key={+day} 
          className={`min-h-[90px] rounded-lg border p-2 relative ${
            isToday(day) ? "border-primary bg-primary/5" : "border-border bg-card"
          } hover:bg-accent/50 transition-colors`}
        >
          <div className="text-xs font-semibold text-foreground">{format(day, "d")}</div>
          <div className="mt-1 space-y-1">
            {bookings
              .filter((b) => day >= startOfMonth(month) && day <= endOfMonth(month) && day >= b.startDate && day <= b.endDate)
              .map((b) => (
                <button
                  key={b.id}
                  onClick={() => onSelectBooking(b.id)}
                  className={`w-full text-left text-[11px] px-2 py-1 rounded-md truncate transition-colors hover:bg-background/80 ${
                    STATUS_MAP[b.status]?.color || "bg-muted text-muted-foreground"
                  }`}
                  title={`${b.id} • ${b.customer.name}`}
                >
                  {b.pod} — {b.customer.name.split(" ")[0]}
                </button>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
}

function StatCard({ icon: Icon, label, value }: { icon: any; label: string; value: string | number }) {
  return (
    <div className="bg-card rounded-lg border p-6 shadow-sm">
      <div className="flex items-center justify-between mb-2">
        <h3 className="text-sm font-medium text-muted-foreground">{label}</h3>
        <Icon className="h-5 w-5 text-muted-foreground" />
      </div>
      <div className="text-2xl font-bold text-foreground">{value}</div>
    </div>
  );
}

export default function KitchenRescueAdmin() {
  const [month, setMonth] = useState(new Date());
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState<string | null>(null);
  const [bookings, setBookings] = useState<Booking[]>(MOCK_BOOKINGS);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showNewBookingDialog, setShowNewBookingDialog] = useState(false);

  const filtered = bookings.filter((b) => {
    if (!query?.trim()) return true;
    const q = query.toLowerCase();
    return [b.id, b.customer.name, b.customer.email, b.customer.address, b.pod, b.status]
      .join(" ")
      .toLowerCase()
      .includes(q);
  });

  const selected = bookings.find((b) => b.id === selectedId) || null;

  // Simple login check - replace with proper authentication
  useEffect(() => {
    const token = localStorage.getItem('admin_token');
    setIsLoggedIn(!!token);
  }, []);

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    const username = formData.get('username');
    const password = formData.get('password');

    // Simple hardcoded login - replace with proper authentication
    if (username === 'admin' && password === 'kitchenrescue2024') {
      localStorage.setItem('admin_token', 'dummy_token');
      setIsLoggedIn(true);
    } else {
      alert('Invalid credentials');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('admin_token');
    setIsLoggedIn(false);
  };

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-8">
          <div className="flex items-center gap-3 mb-8">
            <div className="p-2 bg-primary rounded-lg">
              <Truck className="h-6 w-6 text-primary-foreground" />
            </div>
            <h1 className="text-2xl font-bold text-foreground">Kitchen Rescue Admin</h1>
          </div>

          <form onSubmit={handleLogin} className="space-y-6">
            <div className="space-y-2">
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                name="username"
                type="text"
                placeholder="Enter username"
                required
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                name="password"
                type="password"
                placeholder="Enter password"
                required
              />
            </div>
            <Button type="submit" className="w-full">
              Sign In
            </Button>
          </form>

          <div className="mt-6 text-xs text-muted-foreground text-center">
            Demo credentials: admin / kitchenrescue2024
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-muted/50">
      {/* Top bar */}
      <header className="sticky top-0 z-20 border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
        <div className="mx-auto max-w-7xl px-4 py-4 flex items-center gap-3">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-primary rounded-lg">
              <Truck className="h-5 w-5 text-primary-foreground" />
            </div>
            <span className="font-semibold text-foreground">Kitchen Rescue — Admin</span>
          </div>
          <div className="ml-auto flex items-center gap-2">
            <Button variant="ghost" size="sm">
              <Settings className="h-4 w-4"/>
            </Button>
            <Button variant="outline" size="sm" className="gap-2" onClick={handleLogout}>
              <LogOut className="h-4 w-4"/>Log out
            </Button>
          </div>
        </div>
      </header>

      {/* Content */}
      <main className="mx-auto max-w-7xl p-6">
        {/* KPI row */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard icon={CalendarIcon} label="Pods on hire" value={bookings.length} />
          <StatCard icon={CreditCard} label="Deposits pending" value={bookings.filter(b=>b.payment.status!=="Deposit paid").length} />
          <StatCard icon={Users} label="Customers" value={new Set(bookings.map(b=>b.customer.email)).size} />
          <StatCard icon={Wallet} label="This month revenue" value={`£${bookings.reduce((s,b)=> s + (b.payment.status==="Deposit paid"? b.payment.amount:0),0).toFixed(0)}`} />
        </div>

        {/* Toolbar */}
        <div className="flex flex-col md:flex-row md:items-center gap-4 mb-8">
          <div className="relative w-full md:w-80">
            <Search className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground"/>
            <Input 
              value={query} 
              onChange={(e)=>setQuery(e.target.value)} 
              placeholder="Search bookings, customers, addresses…" 
              className="pl-10"
            />
          </div>

          <div className="ml-auto flex items-center gap-3">
            <div className="flex items-center gap-2 bg-background rounded-lg border p-1">
              <Button variant="ghost" size="sm" onClick={()=>setMonth(subMonths(month,1))}>
                <ChevronLeft className="h-4 w-4"/>
              </Button>
              <div className="px-3 text-sm font-medium w-40 text-center">{format(month, "MMMM yyyy")}</div>
              <Button variant="ghost" size="sm" onClick={()=>setMonth(addMonths(month,1))}>
                <ChevronRight className="h-4 w-4"/>
              </Button>
            </div>

            <Button onClick={() => setShowNewBookingDialog(true)}>
              <Plus className="h-4 w-4 mr-2"/>New booking
            </Button>
          </div>
        </div>

        {/* Two-column layout: calendar + lists */}
        <div className="grid grid-cols-1 xl:grid-cols-3 gap-8">
          <div className="xl:col-span-2 bg-card rounded-lg border shadow-sm">
            <div className="p-6">
              <h2 className="text-lg font-semibold flex items-center gap-2 mb-4">
                <CalendarDays className="h-5 w-5"/> Calendar
              </h2>
              <p className="text-sm text-muted-foreground mb-6">Month view of bookings. Click a booking to view details.</p>
              <MonthCalendar month={month} bookings={filtered} onSelectBooking={setSelectedId} />
            </div>
          </div>

          <div className="bg-card rounded-lg border shadow-sm">
            <div className="p-6">
              <h2 className="text-lg font-semibold flex items-center gap-2 mb-4">
                <CreditCard className="h-5 w-5"/> Payments
              </h2>
              <p className="text-sm text-muted-foreground mb-6">Recent deposits & balances</p>
              <div className="space-y-3">
                {bookings.slice(0, 5).map((b) => (
                  <div 
                    key={b.id} 
                    className="flex items-center justify-between p-3 rounded-lg border hover:bg-accent/50 cursor-pointer transition-colors"
                    onClick={()=>setSelectedId(b.id)}
                  >
                    <div>
                      <div className="font-medium text-sm">{b.id}</div>
                      <div className="text-xs text-muted-foreground">{b.customer.name}</div>
                    </div>
                    <div className="text-right">
                      <Badge variant={STATUS_MAP[b.status]?.variant || "default"} className="text-xs">
                        {b.payment.status}
                      </Badge>
                      <div className="text-sm font-medium mt-1">£{b.payment.amount.toFixed(0)}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Customers table */}
        <div className="bg-card rounded-lg border shadow-sm mt-8">
          <div className="p-6">
            <h2 className="text-lg font-semibold flex items-center gap-2 mb-4">
              <Users className="h-5 w-5"/> Customers
            </h2>
            <p className="text-sm text-muted-foreground mb-6">Contact details and current hire info</p>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Customer</TableHead>
                  <TableHead>Contact</TableHead>
                  <TableHead>Address</TableHead>
                  <TableHead>Booking</TableHead>
                  <TableHead>Status</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filtered.map((b) => (
                  <TableRow key={b.id} className="hover:bg-accent/50">
                    <TableCell className="font-medium">{b.customer.name}</TableCell>
                    <TableCell>
                      <div className="space-y-1">
                        <div className="flex items-center gap-2 text-sm">
                          <Mail className="h-3 w-3 text-muted-foreground"/>
                          {b.customer.email}
                        </div>
                        <div className="flex items-center gap-2 text-sm text-muted-foreground">
                          <Phone className="h-3 w-3"/>
                          {b.customer.phone}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2 text-sm">
                        <MapPin className="h-3 w-3 text-muted-foreground"/>
                        {b.customer.address}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm">
                        <div className="font-medium">{b.pod}</div>
                        <div className="text-muted-foreground">
                          {format(b.startDate, "d MMM")} – {format(b.endDate, "d MMM")}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant={STATUS_MAP[b.status]?.variant || "default"}>
                        {b.status}
                      </Badge>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </div>
      </main>

      {/* New Booking Dialog */}
      <Dialog open={showNewBookingDialog} onOpenChange={setShowNewBookingDialog}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Create booking</DialogTitle>
            <DialogDescription>Add a new temporary kitchen hire.</DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Customer name</Label>
                <Input placeholder="Full name" />
              </div>
              <div className="space-y-2">
                <Label>Email</Label>
                <Input placeholder="email@domain.com" />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Phone</Label>
                <Input placeholder="+44" />
              </div>
              <div className="space-y-2">
                <Label>Address</Label>
                <Input placeholder="Delivery address" />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Pod</Label>
                <select className="w-full h-10 rounded-md border border-input bg-background px-3 text-sm">
                  <option>16ft Pod</option>
                  <option>10ft Pod</option>
                </select>
              </div>
              <div className="space-y-2">
                <Label>Status</Label>
                <select className="w-full h-10 rounded-md border border-input bg-background px-3 text-sm">
                  <option>Awaiting deposit</option>
                  <option>Confirmed</option>
                  <option>Cancelled</option>
                </select>
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Start date</Label>
                <Input type="date" />
              </div>
              <div className="space-y-2">
                <Label>End date</Label>
                <Input type="date" />
              </div>
            </div>
            <div className="space-y-2">
              <Label>Notes</Label>
              <Textarea placeholder="Access notes, packs, other details"/>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowNewBookingDialog(false)}>Cancel</Button>
            <Button>Save booking</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Booking Details Sheet */}
      {selected && (
        <Dialog open={!!selected} onOpenChange={(o)=>!o && setSelectedId(null)}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <div className="p-1 bg-primary rounded">
                  <Truck className="h-4 w-4 text-primary-foreground" />
                </div>
                {selected.id} • {selected.pod}
              </DialogTitle>
              <DialogDescription>
                {format(selected.startDate, "EEE d MMM yyyy")} – {format(selected.endDate, "EEE d MMM yyyy")} • 
                <Badge variant={STATUS_MAP[selected.status]?.variant || "default"} className="ml-2">
                  {selected.status}
                </Badge>
              </DialogDescription>
            </DialogHeader>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 py-4">
              <div className="bg-muted/50 rounded-lg p-4">
                <h3 className="text-sm font-semibold mb-3 flex items-center gap-2">
                  <Users className="h-4 w-4" />
                  Customer Details
                </h3>
                <div className="space-y-2 text-sm">
                  <div className="font-medium">{selected.customer.name}</div>
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <Mail className="h-3 w-3"/>{selected.customer.email}
                  </div>
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <Phone className="h-3 w-3"/>{selected.customer.phone}
                  </div>
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <MapPin className="h-3 w-3"/>{selected.customer.address}
                  </div>
                </div>
              </div>

              <div className="bg-muted/50 rounded-lg p-4">
                <h3 className="text-sm font-semibold mb-3 flex items-center gap-2">
                  <CreditCard className="h-4 w-4" />
                  Payment Details
                </h3>
                <div className="space-y-2 text-sm">
                  <div>Status: <Badge variant={selected.payment.status === "Deposit paid" ? "success" : "warning"} className="ml-2">
                    {selected.payment.status}
                  </Badge></div>
                  <div>Deposit: £{selected.payment.amount.toFixed(0)} ({selected.payment.method})</div>
                  <div className="text-xs text-muted-foreground">Balance auto-invoice 7 days before delivery</div>
                  <div className="pt-2 flex gap-2">
                    <Button size="sm" variant="outline">Send payment link</Button>
                    <Button size="sm">Mark as paid</Button>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-muted/50 rounded-lg p-4">
              <h3 className="text-sm font-semibold mb-3 flex items-center gap-2">
                <Clock className="h-4 w-4" />
                Delivery Details
              </h3>
              <div className="space-y-2 text-sm">
                <div>Date/time: {format(selected.delivery.date, "EEE d MMM, HH:mm")}</div>
                <div>Driver: {selected.delivery.driver}</div>
                <div className="text-xs text-muted-foreground">{selected.delivery.notes}</div>
                <div className="pt-2 flex gap-2">
                  <Button size="sm" variant="outline">Assign driver</Button>
                  <Button size="sm" variant="outline">Export job sheet</Button>
                </div>
              </div>
            </div>

            <div className="bg-muted/50 rounded-lg p-4">
              <h3 className="text-sm font-semibold mb-3">Internal Notes</h3>
              <Textarea defaultValue={selected.notes} className="min-h-[100px]"/>
              <div className="pt-2 text-right">
                <Button size="sm">Save notes</Button>
              </div>
            </div>

            <div className="flex justify-between pt-4">
              <Button variant="destructive" size="sm">Cancel booking</Button>
              <div className="flex gap-2">
                <Button variant="outline" size="sm">Email customer</Button>
                <Button size="sm">Edit booking</Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}
